import React, { useState } from 'react';

const newsletters = [
  // Sample newsletter data with attributes: "Topic", "Frequency", "Content Type"
  // Replace placeholders with actual data
  { name: 'Newsletter 1', logo: 'logo1.png', description: 'Description of Newsletter 1', topic: ['Climate', 'Technology'], frequency: ['Daily'], content: 'Short' },
  { name: 'Newsletter 2', logo: 'logo2.png', description: 'Description of Newsletter 2', topic: ['Travel'], frequency: ['Weekly'], content: 'Medium' },
  // Add more newsletters...
];

const App = () => {
  const [step, setStep] = useState(1);
  const [answers, setAnswers] = useState({});

  const [selectedOptions, setSelectedOptions] = useState([]);
  const topicOptions = ['Climate', 'Technology', 'Business', 'Travel', 'Sports', 'Lifestyle', 'Productivity', 'Arts'];
  const frequencyOptions = ['Daily', 'Biweekly', 'Weekly'];
  const contentOptions = ['Short', 'Medium', 'Long'];



  const handleAnswer = (question, answer) => {
    setAnswers(prevAnswers => ({
      ...prevAnswers,
      [question]: answer  // Use the answer parameter directly
    }));    
    setStep(step + 1);
    console.log(step); 
    recommendNewsletters();
  };  

  const handleCheckboxChange = (option) => {
    setAnswers(prevAnswers => ({
      ...prevAnswers,
      1: prevAnswers[1] ? [...prevAnswers[1], option] : [option],

    }));
  };

  const handleCheckboxChange2 = (option) => {
    setAnswers(prevAnswers => ({
      ...prevAnswers,
      2: prevAnswers[2] ? [...prevAnswers[2], option] : [option],

    }));
  };

  const handleNextClick = () => {
    setStep(step + 1);
    console.log("Selected topics:", answers[1]);
    console.log("Selected frequencies:", answers[2]);
    console.log("Selected topics:", answers[3]);
    console.log("Selected topics:", answers[4]);

  };

  const recommendNewsletters = () => {
    const userTopics = answers[1] || [];
    const userFrequencies = answers[2] || [];
    const userContentPreference = answers[3];
    const userPersonalization = answers[4];

    const recommendedNewsletters = newsletters.map(newsletter => {
      let points = 0;

      newsletter.topic.forEach(topic => {
        if (userTopics.includes(topic)) {
          points++;
        }
      });

      newsletter.frequency.forEach(frequency => {
        if (userFrequencies.includes(frequency)) {
          points++;
        }
      });

      if (newsletter.content === userContentPreference) {
        points++;
      }

      if (userPersonalization === "Highly personalized") {
        points++;
      } else if (userPersonalization === "I'm open to try something new") {
        points += 0.5;
      }

      return { ...newsletter, points };
    });

    const sortedRecommendedNewsletters = recommendedNewsletters.sort((a, b) => b.points - a.points).slice(0, 5);
    console.log(sortedRecommendedNewsletters);
    setRecommended(sortedRecommendedNewsletters);
  };


  const [recommended, setRecommended] = useState([]);

  const renderQuestion = () => {
    switch (step) {
      case 1:
        return (
          <div>
            <h2 className="mb-4">Pick Topics You're Interested In</h2>
            <div>
              <h1>Select all that apply:</h1>
              {topicOptions.map((option, index) => (
                <label key={index}>
                  <input 
                    type="checkbox" 
                    className="btn-check" 
                    id="btn-check-outlined" 
                    autocomplete="off"
                    checked={(answers[1] || []).includes(option)}
                    onChange={() => handleCheckboxChange(option)}
                  />
                  {option}
                </label>
              ))}
              <button onClick={handleNextClick}>Next</button>
            </div>

          </div>
        );

      case 2:
        return (
          <div>
            <h2 className="mb-4">Pick Topics You're Interested In</h2>
            <div>
              <h1>Select all that apply:</h1>
              {frequencyOptions.map((option, index) => (
                <label key={index}>
                  <input className = "m-2"
                    type="checkbox"
                    checked={(answers[2] || []).includes(option)}
                    onChange={() => handleCheckboxChange2(option)}
                  />
                  {option}
                </label>
              ))}
              <button onClick={handleNextClick}>Next</button>
            </div>

          </div>

        );

        case 3:
          return (
            <div>
              <h2 className="mb-4">What is the ideal length of the emails?</h2>
              <div className="d-flex flex-wrap">
                {/* Replace with your checkbox options */}
                <label className="form-check-label mr-3">
                  <input type="checkbox" className="form-check-input" value="short" onChange={() => handleAnswer(3, ['short'])} /> Short (1-2 minutes)
                  <input type="checkbox" className="form-check-input" value="medium" onChange={() => handleAnswer(3, ['medium'])} /> Medium (3-5 minutes)
                  <input type="checkbox" className="form-check-input" value="long" onChange={() => handleAnswer(3, ['long'])} /> Long (5+ minutes)
                  <input type="checkbox" className="form-check-input" value="all" onChange={() => handleAnswer(3, ['all'])} /> I'm fine with any length
                </label>
              </div>
            </div>
          );

        case 4:
        return (
          <div>
            <h2 className="mb-4">How personalized would you like the recommendations to be?</h2>
            <div className="d-flex flex-wrap">
              {/* Replace with your checkbox options */}
              <label className="form-check-label mr-3">
                <input type="checkbox" className="form-check-input" value="daily" onChange={() => handleAnswer(4, ['daily'])} /> Highly personalized
                <input type="checkbox" className="form-check-input" value="multiple" onChange={() => handleAnswer(4, ['multiple'])} /> I'm open to try something new 
              </label>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  const renderRecommendations = () => {
    return (
      <div>
        <h2> hi</h2>
        <h2 className="mb-4">Recommended Newsletters</h2>
        <div className="card-deck">
          {recommended.map((newsletter, index) => (
            <div key={index} className="card mb-4">
              <img src={newsletter.logo} className="card-img-top" alt={newsletter.name} style={{ maxWidth: '100px', margin: 'auto' }} />
              <div className="card-body">
                <h5 className="card-title">{newsletter.name}</h5>
                <p className="card-text">{newsletter.description}</p>
                <a href="#" className="btn btn-primary">Subscribe</a>
              </div>
              <div className="card-footer bg-primary text-white text-center">
                Points: {newsletter.points}
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="container">
      <h1 className="mt-4">Email Newsletter Recommender</h1>
      {step <= 4 ? renderQuestion() : renderRecommendations()}
    </div>
  );
};

export default App;
